/**
 * Copyright 2020-present, Mong Lê Văn , Cop.
 * All rights reserved.
 *
 * @author Mong Lê Văn  on 4/10/2020.
 */
import React from 'react';
import { Form, Input, Button, Checkbox, message } from 'antd';
import {NavLink } from 'react-router-dom';

// firebasefirebase
import database from '../firebase/firebase';

const locationLogInHome = {
  pathname: '/',
}

// const
const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};
const tailLayout = {
  wrapperCol: {
    offset: 8,
    span: 16,
  },
};

class SignUp extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            user: '',
            pass: '',
        };
    }

    onChangeUser = (event) => {
        const textUser = event.target.value;
        this.setState({ user: textUser});
    };

    onChangePass = (event) => {
        const textPass = event.target.value;
        this.setState({ pass: textPass});
    };
    
    onSignUp = () => {
        const {user, pass} = this.state;
        // let history = useHistory().useContext(Context);
        debugger;
        database.auth()
        .createUserWithEmailAndPassword(user, pass)
        .then(this.onLogInHome)
        .catch((error) => message.info('Thất bại lỗi :'+error))
        this.setState({
          user: '',
          pass: '',
        });
        debugger;
    };

    onLogInHome = () => {
      const { history} = this.props;
      debugger;
      history.push();
      debugger;
    };

    render(){
        const {user, pass} = this.state;
        return (
          <div class="flex col-2 row" id="form">
          <div class="left col">
            <div class="col-spacer">
              <div class="form-header"><i class="fas fa-lock icon txtglow"></i><span>Login Form</span></div>
            </div>
            <div class="input-wrap">
              <div class="input-icon">
                <div class="icon"><i class="fas fa-user"></i></div>
                <input type="text" placeholder="Username or Email" onChange={this.onChangeUser} />
              </div><a class="input-desc" href="#">Forgot your username?</a>
            </div>
            <div class="input-wrap"> 
              <div class="input-icon">
                <div class="icon"><i class="fas fa-key"></i></div>
                <input type="password" placeholder="Password" onChange={this.onChangePass}/>
              </div><a class="input-desc" href="#">Forgot your password?</a>
            </div>
            <div class="flex space mt-center">
              <button class="signup big" onClick={this.onSigUp} >SAVE-NOW</button>
              <button class="primary big" onClick={this.onLogin}>RESET-NOW</button>
            </div>
          </div>
        </div>
        );
    }
}
export default SignUp;
